import React, { useState } from 'react';
import axios from 'axios';
import './CreateForm.css';

const API_URL = 'http://localhost:5000';

const CreateForm = () => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    duration:'',
    activity:'',
    date:'',
    price:''
  });

  const handleInputChange = (event) => {
    if (event.target.name === 'image') {
      // Удаляем элемент image из объекта formData
      const { image, ...formDataWithoutImage } = formData;
      setFormData(formDataWithoutImage);
    } else {
      setFormData({
        ...formData,
        [event.target.name]: event.target.value,
      });
    }
  };
  

  const handleSubmit = async (event) => {
    event.preventDefault();

    console.log(formData);

    const token = localStorage.getItem('token');
    const config = {
      headers: {
        'Content-Type': 'multipart/form-data',
        Authorization: `Bearer ${token}`,
      },
    };

    const formDataToSend = new FormData();
    formDataToSend.append('title', formData.title);
    formDataToSend.append('description', formData.description);
    formDataToSend.append('duration', formData.duration);
    formDataToSend.append('activity', formData.activity);
    formDataToSend.append('date', formData.date);
    formDataToSend.append('price', formData.price);

    try {
      await axios.post(`${API_URL}/api/my_tour`, formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`,
        },
      });
  
      alert('Запись успешно создана');
      setFormData({
        title: '',
        description: '',
        duration: '',
        activity: '',
        date: '',
        price: '',
      });
    } catch (error) {
      console.log('Ошибка создания записи', error);
    }
  };

  return (
    <div className="create-form-container">
       <h2>Создать запись</h2>
    <form onSubmit={handleSubmit} className="create-form">
        <div className="form-group">
        <label>Название:</label>
        <input type="text" name="title" value={formData.title} onChange={handleInputChange} required />
      </div>
      <div className="form-group">
        <label>Описание:</label>
        <textarea  type="text" name="description" value={formData.description} onChange={handleInputChange} required />
      </div>
      <div className="form-group">
        <label>Длительность:</label>
        <input type="text" name="duration" value={formData.duration} onChange={handleInputChange} required />
      </div>
      <div className="form-group">
        <label>Вид активности:</label>
        <input type="text" name="activity" value={formData.activity} onChange={handleInputChange} required />
      </div>
      <div className="form-group">
        <label>Дата:</label>
        <input type="date" name="date" value={formData.date} onChange={handleInputChange} required />
      </div>
      <div className="form-group">
        <label>Цена:</label>
        <input type="text" name="price"  value={formData.price} onChange={handleInputChange} required />
      </div>
      <button type="submit" className="btn-submit">Создать</button>
    </form>
    </div>
  );
};

export default CreateForm;
