import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { API_URL } from '../config';

const TourDetailsPage = () => {
  const { tourId } = useParams();
  const [tour, setTour] = useState(null);

  useEffect(() => {
    const fetchTourData = async () => {
      try {
        const response = await axios.get(`${API_URL}/api/my_tour/${tourId}`);
        setTour(response.data);
      } catch (error) {
        console.error('Ошибка получения данных тура', error);
      }
    };

    fetchTourData();
  }, [tourId]);

  const handleDownload = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/my_tour/${tourId}/download`, {
        responseType: 'blob',
      });
      const blob = new Blob([response.data]);

      // Создаем ссылку на файл и автоматически кликаем по ней для скачивания файла
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = 'filename.extension'; // Замените на имя файла и расширение, которые вы хотите использовать
      link.click();

      // Освобождаем ресурсы URL объекта
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Произошла ошибка при скачивании файла:', error);
    }
  };

  return (
    <div>
      {tour ? (
        <div>
          <h2>{tour.title}</h2>
          <p>{tour.description}</p>
          <button onClick={handleDownload}>Скачать файл</button>
        </div>
      ) : (
        <p>Загрузка данных тура...</p>
      )}
    </div>
  );
};

export default TourDetailsPage;
