import React from 'react';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import Banner from '../components/Banner';
import Section from '../components/Section';
import Attractions from './Attractions';
import Tours from './Tours';
import History from './History';
import '../styles.css';

const Home = () => {
  return (
    <div className="home">
     
      <Banner 
        title="Добро пожаловать в Кыргызстан"
        subtitle="Откройте для себя красоту и уникальность этой удивительной страны"
        imageUrl={require('./MyCollages.jpg')}
      />
      <div className="banner-text">
       
      </div>
      <div className="sections-container">
      <Link to="/history" className="section-link">
          <Section
            title="О Кыргызстане"
            description="Исследуйте богатое наследие и историю Кыргызстана через его культурные памятники и традиции."
            image='https://marakandatravel.asia/wp-content/uploads/2019/12/68.jpg'
          />
        </Link>
        
        <Link to="/tours" className="section-link">
          <Section
            title="Туры и путешествия"
            description="Присоединяйтесь к нашим увлекательным турам и откройте настоящее волшебство Кыргызстана."
            image='https://tropki.ru/images/excursions/aa27f834-b5a4-11eb-b6b8-16f465951405/bishkek-trekking-k-ozeru-kel-tor.jpg'
          />
        </Link>

        <Link to="/attractions" className="section-link">
          <Section
            title="Ландшафт"
            description="Откройте для себя удивительные пейзажи, богатую культуру и гостеприимство Кыргызстана."
            image='https://www.centralasia-travel.com/upload/tiles/celestial_mountains.jpg'
          />
        </Link>
        
      </div>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Switch>
        <Route exact path="/" component={Home} />
        <Route path="/attractions" component={Attractions}/>
        <Route path="/tours" component={Tours} />
        <Route path="/history" component={History} />
      
      </Switch>
    </Router>
  );
};

export default App;
