import React from 'react';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import Banner from '../components/Banner';
import Section from '../components/Section';
import Attractions from './Locations';
import Rentals from './Rentals';
import About from './About';
import '../styles.css';

const Home = () => {
  return (
    <div className="home">
      <Banner 
        title="Аренда горнолыжного снаряжения"
        subtitle="Лучшее снаряжение для вашего отдыха в горах"
        imageUrl={require('./ski-banner.jpg')}
      />
      <div className="banner-text">
        <p>
          Добро пожаловать в наш сервис аренды горнолыжного снаряжения! Мы предлагаем широкий выбор
          лыж, сноубордов, ботинок и экипировки для всех уровней катания – от начинающих до профессионалов.
        </p>
        <p>
          Забронируйте нужное оборудование заранее и наслаждайтесь комфортным катанием на лучших
          горнолыжных курортах. Мы гарантируем качественное обслуживание, доступные цены и удобную систему
          аренды.
        </p>
        <p>
          Наш прокатный пункт расположен вблизи популярных трасс, что позволяет вам быстро получить
          снаряжение и отправиться на склон без лишних хлопот.
        </p>
      </div>
      <div className="sections-container">
        <Link to="/about" className="section-link">
          <Section
            title="О нас"
            description="Узнайте больше о нашем сервисе аренды и преимуществах для клиентов."
            image='https://example.com/about-image.jpg'
          />
        </Link>
        
        <Link to="/rentals" className="section-link">
          <Section
            title="Каталог снаряжения"
            description="Выберите и забронируйте качественное горнолыжное снаряжение."
            image='https://example.com/equipment.jpg'
          />
        </Link>

        <Link to="/attractions" className="section-link">
          <Section
            title="Горнолыжные курорты"
            description="Откройте лучшие трассы и курорты для незабываемого катания."
            image='https://example.com/resorts.jpg'
          />
        </Link>
      </div>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Switch>
        <Route exact path="/" component={Home} />
        <Route path="/attractions" component={Attractions}/>
        <Route path="/rentals" component={Rentals} />
        <Route path="/about" component={About} />
      </Switch>
    </Router>
  );
};

export default App;
