import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './DeleteForm.css';

const API_URL = 'http://localhost:5000';

const DeleteForm = () => {
  const [records, setRecords] = useState([]);
  const [selectedRecordId, setSelectedRecordId] = useState('');

  useEffect(() => {
    const fetchRecords = async () => {
      try {
        const response = await axios.get(`${API_URL}/api/my_tour`);
        setRecords(response.data);
      } catch (error) {
        console.log('Ошибка получения записей', error);
      }
    };

    fetchRecords();
  }, []);

  const handleRecordChange = (event) => {
    setSelectedRecordId(event.target.value);
  };

  const handleDelete = async () => {
    const token = localStorage.getItem('token');

    try {
      await axios.delete(`${API_URL}/api/my_tour/${selectedRecordId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      alert('Запись успешно удалена');
      setSelectedRecordId('');
    } catch (error) {
      console.log('Ошибка удаления записи', error);
    }
  };

  return (
    <div className="delete-form-container">
      <h2>Удалить запись</h2>
      <div>
        <select value={selectedRecordId} onChange={handleRecordChange}>
          <option value="">Выберите запись</option>
          {records.map((record) => (
            <option key={record.id} value={record.id}>
              {record.name}
            </option>
          ))}
        </select>
      </div>
      {selectedRecordId && (
        <div>
          <button onClick={handleDelete}className="btn-delete">Удалить</button>
        </div>
      )}
    </div>
  );
};

export default DeleteForm;
