import React, { useState, useEffect } from 'react';
import axios from 'axios';
import UpdateForm from './UpdateForm';
import DeleteForm from './DeleteForm';

const API_URL = 'http://localhost:5000';

const ReadForm = ({ handleToggleUpdateForm }) => {
  const [records, setRecords] = useState([]);
  const [selectedRecord, setSelectedRecord] = useState(null);

  useEffect(() => {
    fetchRecords();
  }, []);
  console.log(records);

  const fetchRecords = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/equipment`);
      setRecords(response.data);
    } catch (error) {
      console.log('Ошибка получения записей', error);
    }
  };

  const handleToggleDeleteForm = (recordId) => {
    const record = records.find((r) => r.id === recordId);
    setSelectedRecord(record);
  };

  const handleDeleteRecord = async (recordId) => {
    try {
      await axios.delete(`${API_URL}/api/my_tour/${recordId}`);
      fetchRecords();
    } catch (error) {
      console.log('Ошибка при удалении записи', error);
    }
  };

  const handleUpdateRecord = (record) => {
    setSelectedRecord(record);
    handleToggleUpdateForm(record.tour_id);
  };

  return (
    <div>
      <h2>Просмотр записей</h2>
      <table>
        <thead>
          <tr>
            <th>Название</th>
            <th>Описание</th>
            <th>Длительность</th>
            <th>Вид активности</th>
            <th>Дата</th>
            <th>Цена</th>
            <th>Действия</th>
          </tr>
        </thead>
        <tbody>
          {records.map((record) => (
            <tr key={record.id}>
              <td>{record.title}</td>
              <td>{record.description}</td>
              <td>{record.duration}</td>
              <td>{record.activity}</td>
              <td>{record.date}</td>
              <td>{record.price}</td>
              <td>
                <button onClick={() => handleUpdateRecord(record)}>Изменить</button>
                <button onClick={() => handleToggleDeleteForm(record.id)}>Удалить</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {selectedRecord && (
        <div>
          <UpdateForm
            recordId={selectedRecord.id}
            onCancel={() => setSelectedRecord(null)}
          />
          <DeleteForm
            recordId={selectedRecord.id}
            handleDeleteRecord={handleDeleteRecord}
            onCancel={() => setSelectedRecord(null)}
          />
        </div>
      )}
    </div>
  );
};

export default ReadForm;
