import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import axios from 'axios';

const API_URL = 'http://localhost:5000';

const useFetchUserData = (token) => {
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserData = () => {
      axios
        .get(`${API_URL}/api/user-data`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((response) => {
          setUserData(response.data);
          setLoading(false);
        })
        .catch((error) => {
          console.log('Ошибка получения данных пользователя', error);
          setLoading(false);
        });
    };

    fetchUserData();
  }, [token]);

  if (loading) {
    return <p>Загрузка данных...</p>;
  }

  return userData;
};

const User = () => {
  const dispatch = useDispatch();
  const token = localStorage.getItem('token');
  const userData = useFetchUserData(token);

  const handleLogout = () => {
    localStorage.removeItem('token');
    dispatch({ type: 'LOGOUT' });
  };

  if (!token) {
    dispatch({ type: 'LOGOUT' });
    return null;
  }

  if (!userData) {
    return <p>Вы вошли в систему</p>;
  }

  return (
    <div className="user_container">
      <p>Добро пожаловать, {userData.name}!</p>
      <p>Имя: {userData.name}</p>
      <p>Фамилия: {userData.last_name}</p>
      <p>Email: {userData.email}</p>
      <p>Номер телефона: {userData.number}</p>
      <button onClick={handleLogout}>Выйти</button>
    </div>
  );
};

export default User;
