import React, { useState } from 'react';
import Navigation from './Navigation';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faTimes, faMountain } from '@fortawesome/free-solid-svg-icons';
import '../styles.css';

const Header = ({ userRole }) => {
  const [isMenuOpen, setMenuOpen] = useState(false);

  const handleMenuToggle = () => {
    setMenuOpen(!isMenuOpen);
  };

  return (
    <header className="header">
      <div className="logo-container">
        <FontAwesomeIcon icon={faMountain} className="mountain-icon" />
        <h1 className="logo">GUIDE.KG</h1>
      </div>
      <button className="menu-toggle" onClick={handleMenuToggle}>
        <FontAwesomeIcon icon={isMenuOpen ? faTimes : faBars} />
      </button>
      <Navigation isMenuOpen={isMenuOpen} handleMenuToggle={handleMenuToggle} userRole={userRole} />
    </header>
  );
};

export default Header;
