import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const TourDetailsPage = () => {
  const [tourData, setTourData] = useState(null);
  const { tourId } = useParams();

  const fetchTourData = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/my_tour/${tourId}`);
      setTourData(response.data);
    } catch (error) {
      console.error('Ошибка загрузки данных о туре:', error);
    }
  };

  const handleDownload = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/my_tour/${tourData.tour_id}/download`, {
        responseType: 'blob',
      });
      const blob = new Blob([response.data]);

      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = tourData.file;
      link.click();

      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Произошла ошибка при скачивании файла:', error);
    }
  };

  useEffect(() => {
    fetchTourData();
  }, []);

  if (!tourData) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>{tourData.title}</h2>
      <p>{tourData.description}</p>
      <button onClick={handleDownload}>Скачать файл</button>
    </div>
  );
};

export default TourDetailsPage;
