import React, { useState } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:5000';

const SignupForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    last_name: '',
    email: '',
    number: '',
    password: '',
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const formDataToSubmit = {
      ...formData,
      number: parseInt(formData.number),
    };

    axios.post(`${API_URL}/api/register`, formDataToSubmit)
      .then(response => {
        // Обработка успешной отправки данных формы
      })
      .catch(error => {
        // Обработка ошибок при отправке данных формы
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="name" placeholder="Имя" value={formData.name} onChange={handleChange} />
      <input type="text" name="last_name" placeholder="Фамилия" value={formData.last_name} onChange={handleChange} />
      <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} />
      <input type="tel" name="number" placeholder="Номер телефона" value={formData.number} onChange={handleChange} />
      <input type="password" name="password" placeholder="Пароль" value={formData.password} onChange={handleChange} />
      <button type="submit">Зарегистрироваться</button>
    </form>
  );
};

export default SignupForm;
