import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const TourDetailsPage = () => {
  const { tourId } = useParams();

  useEffect(() => {
    // Загрузка данных о туре на основе tourId
    const fetchTourData = async () => {
      try {
        const response = await axios.get(`${API_URL}/api/my_tour/${tourId}`);
        // Обновление состояния компонента с данными тура
        // ...
      } catch (error) {
        console.error('Ошибка загрузки данных о туре:', error);
      }
    };

    fetchTourData();
  }, [tourId]);

  const handleDownload = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/my_tour/${tourId}/download`);
      const blob = await response.blob();

      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = 'filename.extension'; 
      link.click();

      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Произошла ошибка при скачивании файла:', error);
    }
  };

  return (
    <div>
      <button onClick={handleDownload}>Скачать файл</button>
    </div>
  );
};

export default TourDetailsPage;
