// === Обновленный фронтенд (React) ===
import React, { useState } from 'react';
import './Equipment.css';

const Equipment = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    title: '',
    description: '',
    category: '',
    price: ''
  });
  const [equipmentData, setEquipmentData] = useState([]);
  const [newEquipment, setNewEquipment] = useState({ title: '', description: '', price: '', image: '' });

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleFilterChange = (event) => {
    const { name, value } = event.target;
    setFilters((prevFilters) => ({ ...prevFilters, [name]: value }));
  };

  const filterEquipment = (equipment) => {
    return equipment.filter((item) => {
      const { category, price } = filters;
      const searchTermMatch =
        item.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        item.description.toLowerCase().includes(searchTerm.toLowerCase());

      return (
        (category === '' || item.category.toLowerCase().includes(category.toLowerCase())) &&
        (price === '' || item.price === price) &&
        searchTermMatch
      );
    });
  };

  const handleAddEquipment = () => {
    if (!newEquipment.title || !newEquipment.description || !newEquipment.price) {
      alert('Заполните все поля');
      return;
    }
    setEquipmentData([...equipmentData, newEquipment]);
    setNewEquipment({ title: '', description: '', price: '', image: '' });
  };

  return (
    <div className="equipment-page">
      <div className="equipment-title">
        <h1>Туры</h1>
      </div>
      <div className="search-filter">
        <input type="text" placeholder="Поиск по названию..." value={searchTerm} onChange={handleSearch} className="search-input" />
        <div className="filter-group">
          <label>Название:</label>
          <input type="text" placeholder="Название" name="title" value={filters.title} onChange={handleFilterChange} className="filter-input" />
        </div>
      </div>

      <div className="add-equipment">
        <h2>Добавить снаряжение</h2>
        <input type="text" placeholder="Название" value={newEquipment.title} onChange={(e) => setNewEquipment({ ...newEquipment, title: e.target.value })} />
        <input type="text" placeholder="Описание" value={newEquipment.description} onChange={(e) => setNewEquipment({ ...newEquipment, description: e.target.value })} />
        <input type="number" placeholder="Цена" value={newEquipment.price} onChange={(e) => setNewEquipment({ ...newEquipment, price: e.target.value })} />
        <button onClick={handleAddEquipment}>Добавить</button>
      </div>

      <div className="equipment-container">
        {filterEquipment(equipmentData).map((item, index) => (
          <div className="equipment-card" key={index}>
            <div className="equipment-details">
              <h3>{item.title}</h3>
              <p>{item.description}</p>
              <span className="equipment-price">{item.price}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Equipment;
