import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:5000';

const UpdateForm = ({ recordId }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    image: null,
    duration: '',
    activity: '',
    date: '',
    price: ''
  });
  const [isLoading, setIsLoading] = useState(false);
  const [isDataLoaded, setIsDataLoaded] = useState(false); // Добавлено состояние для проверки загрузки данных

  useEffect(() => {
    const fetchRecordData = async () => {
      try {
        const response = await axios.get(`${API_URL}/api/my_tour/${recordId}`);
        const recordData = response.data;
        setFormData({
          title: recordData.title,
          description: recordData.description,
          image: null,
          duration: recordData.duration,
          activity: recordData.activity,
          date: recordData.date,
          price: recordData.price
        });
        setIsDataLoaded(true); // Устанавливаем состояние, когда данные загружены
      } catch (error) {
        console.log('Ошибка получения данных записи', error);
      }
    };

    if (recordId) {
      fetchRecordData();
    }
  }, [recordId]);

  const handleInputChange = (event) => {
    if (event.target.name === 'image') {
      setFormData({
        ...formData,
        [event.target.name]: event.target.files[0],
      });
    } else {
      setFormData({
        ...formData,
        [event.target.name]: event.target.value,
      });
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    setIsLoading(true);

    const token = localStorage.getItem('token');
    const config = {
      headers: {
        'Content-Type': 'multipart/form-data',
        Authorization: `Bearer ${token}`,
      },
    };

    const formDataToSend = new FormData();
    formDataToSend.append('title', formData.title);
    formDataToSend.append('description', formData.description);
    formDataToSend.append('image', formData.image);
    formDataToSend.append('duration', formData.duration);
    formDataToSend.append('activity', formData.activity);
    formDataToSend.append('date', formData.date);
    formDataToSend.append('price', formData.price);

    try {
      await axios.put(`${API_URL}/api/my_tour/${recordId}`, formDataToSend, config);
      alert('Запись успешно обновлена');
    } catch (error) {
      console.log('Ошибка обновления записи', error);
    }

    setIsLoading(false);
  };

  // Добавлена проверка загрузки данных перед отображением формы
  if (!isDataLoaded) {
    return null;
  }

  return (
    <div>
      <h2>Изменить запись</h2>
      <form onSubmit={handleSubmit} className="update-form">
        <div className="form-group">
          <label>Название:</label>
          <input type="text" name="title" value={formData.title} onChange={handleInputChange} required />
        </div>
        <div className="form-group">
          <label>Описание:</label>
          <textarea name="description" value={formData.description} onChange={handleInputChange} required />
        </div>
        <div className="form-group">
          <label>Изображение:</label>
          <input type="file" name="image" onChange={handleInputChange} />
        </div>
        <div className="form-group">
          <label>Длительность:</label>
          <input type="text" name="duration" value={formData.duration} onChange={handleInputChange} required />
        </div>
        <div className="form-group">
          <label>Вид активности:</label>
          <input type="text" name="activity" value={formData.activity} onChange={handleInputChange} required />
        </div>
        <div className="form-group">
          <label>Дата:</label>
          <input type="date" name="date" value={formData.date} onChange={handleInputChange} required />
        </div>
        <div className="form-group">
          <label>Цена:</label>
          <input type="text" name="price" value={formData.price} onChange={handleInputChange} required />
        </div>
        <button type="submit" disabled={isLoading}>Сохранить</button>
      </form>
    </div>
  );
};

export default UpdateForm;
