import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../../styles.css';

const API_URL = 'http://localhost:5000';

const ReadForm = () => {
  const [records, setRecords] = useState([]);

  useEffect(() => {
    const fetchRecords = async () => {
      try {
        const response = await axios.get(`${API_URL}/api/my_tour`);
        setRecords(response.data);
      } catch (error) {
        console.log('Ошибка получения записей', error);
      }
    };

    fetchRecords();
  }, []);

  return (
    <div className="read-form-container">
      <h2>Список записей</h2>
      <table className="records-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Название</th>
            <th>Описание</th>
            <th>Изображение</th>
            <th>Длительность</th>
            <th>Вид активности</th>
            <th>Дата</th>
            <th>Цена</th>
          </tr>
        </thead>
        <tbody>
          {records.map((record) => (
            <tr key={record.id}>
              <td>{record.id}</td>
              <td>{record.title}</td>
              <td>{record.description}</td>
              <td>
                <img src={`data:image/png;base64,${record.image}`} alt="Изображение" width="100" />
              </td>
              <td>{record.duration}</td>
              <td>{record.activity}</td>
              <td>{record.date}</td>
              <td>{record.price}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ReadForm;
