import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const TourDetailsPage = () => {
  const { tourId } = useParams();
  const [tour, setTour] = useState(null);

  useEffect(() => {
    // Функция для загрузки данных тура по идентификатору tourId
    const fetchTour = async () => {
      try {
        const response = await fetch(`/api/my_tour/${tourId}`);
        const data = await response.json();
        setTour(data);
      } catch (error) {
        console.error('Произошла ошибка при загрузке данных тура:', error);
      }
    };

    fetchTour();
  }, [tourId]);

  if (!tour) {
    return <div>Загрузка...</div>;
  }

  // Рендеринг данных тура
  return (
    <div>
      <h2>{tour.title}</h2>
      <p>{tour.description}</p>
      {/* Добавьте остальные данные тура */}
    </div>
  );
};

export default TourDetailsPage;
