import React from 'react';
const API_URL = 'http://localhost:5000';

const TourDetails = ({ tour }) => {
  if (!tour) {
    return <div>Loading...</div>; // Можете заменить на любой загрузочный компонент или текст
  }

  return (
    <div className="tour-details-page">
      <h1>{tour.title}</h1>
      <p>{tour.description}</p>
      <a href={`${API_URL}/uploads/${tour.file}`} download>
        Скачать файл
      </a>
    </div>
  );
};


export default TourDetails;
