import React, { useState } from 'react'; 
import '../styles.css';

const Equipment = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [minPrice, setMinPrice] = useState(0);
  const [maxPrice, setMaxPrice] = useState(1000); // Максимальная цена по умолчанию

  const equipmentList = [
    {
      name: "Сноуборд Черный",
      description: "Этот сноуборд сочетает в себе стильный черный дизайн и отличные характеристики для всех уровней катания.",
      image: "https://avatars.mds.yandex.net/i?id=f0d337a2a0f22f4fd385ab855173b985de18f740-8485986-images-thumbs&n=13",
      price: 120,
    },
    {
      name: "Сноуборд Красный Взрыв",
      description: "Яркий сноуборд красного цвета с отличными характеристиками для маневренности.",
      image: "https://avatars.mds.yandex.net/i?id=e474233e2c6c0f35ff35f416a98cf3a69c608c6c-7012335-images-thumbs&n=13",
      price: 150,
    },
    {
      name: "Лыжы FireTrack 9000",
      description: "Лыжи с динамичным дизайном, подходящие для сложных трасс.",
      image: "https://avatars.mds.yandex.net/i?id=bf3d3f031b4f8afec8b50ae28b0affdd714d5fa6-4592904-images-thumbs&n=13",
      price: 200,
    },
    {
      name: "Куртка Лавина Красный",
      description: "Ярко-красная горнолыжная куртка для отличной защиты от холода.",
      image: "https://avatars.mds.yandex.net/i?id=10adce8e7f586427529c2b5496565b6bcf862ecc-5139788-images-thumbs&n=13",
      price: 180,
    },
    // Добавьте остальные предметы с ценами
  ];

  const filteredEquipment = equipmentList.filter(item =>
    item.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
    item.price >= minPrice && item.price <= maxPrice
  );

  return (
    <div className="equipment-page">
      <div className="equipment-title">
        <h1>Снаряжение для лыж</h1>
      </div>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Поиск..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      <div className="price-filter">
        <h3>Фильтровать по цене:</h3>
        <label>
          Минимальная цена:
          <input
            type="number"
            value={minPrice}
            onChange={(e) => setMinPrice(e.target.value)}
            min="0"
          />
        </label>
        <label>
          Максимальная цена:
          <input
            type="number"
            value={maxPrice}
            onChange={(e) => setMaxPrice(e.target.value)}
            min="0"
          />
        </label>
      </div>

      <div className="grid-container">
        {filteredEquipment.map((item, index) => (
          <div key={index} className="grid-item">
            <img src={item.image} alt={item.name} />
            <h3>{item.name}</h3>
            <p>{item.description}</p>
            <p>Цена: {item.price}₽</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Equipment;
