import React, { useState, useEffect } from 'react';
import axios from 'axios';
import LoginForm from '../components/LoginForm';
import SignupForm from '../components/SignupForm';
import '../styles.css';

const API_URL = 'http://localhost:5000';

const User = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showLoginForm, setShowLoginForm] = useState(true);
  const [showSignupForm, setShowSignupForm] = useState(false);

  useEffect(() => {
    const checkAuthStatus = () => {
      const token = localStorage.getItem('token');

      if (token) {
        // Проверка токена на сервере
        axios
          .get(`${API_URL}/api/check-auth`, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          })
          .then((response) => {
            setIsLoggedIn(true);
          })
          .catch((error) => {
            console.log('Ошибка проверки авторизации', error);
            setIsLoggedIn(false);
          });
      } else {
        setIsLoggedIn(false);
      }
      if (token) {
        axios
          .get(`${API_URL}/api/user`, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          })
          .then((response) => {
            setUserData(response.data);
          })
          .catch((error) => {
            console.log('Ошибка получения данных пользователя', error);
          });
      }
      
    };

    checkAuthStatus();
  }, []);


  const [userData, setUserData] = useState({});
  const [showEditForm, setShowEditForm] = useState(false);


  const handleLogin = (formData) => {
    axios
      .post(`${API_URL}/api/login`, formData)
      .then((response) => {
        const token = response.data.token;
        localStorage.setItem('token', token);
        setIsLoggedIn(true);
        axios
        .get(`${API_URL}/api/user`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((response) => {
          setUserData(response.data);
        })
        .catch((error) => {
          console.log('Ошибка получения данных пользователя', error);
        });
    })
    .catch((error) => {
      console.log('Ошибка авторизации', error);
    });
};


  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
  };

  const handleToggleSignupForm = () => {
    setShowSignupForm(!showSignupForm);
    setShowLoginForm(false);
  };

  const handleToggleLoginForm = () => {
    setShowLoginForm(!showLoginForm);
    setShowSignupForm(false);
  };

  return (
    <div className="user_container">
      {isLoggedIn ? (
        <div>
          <button onClick={handleLogout}>Выйти</button>
          <p>Добро пожаловать, {userData.name}!</p>
        </div>
      ) : (
        <div>
          {!showSignupForm && (
            <div className="form-group">
              <LoginForm onLogin={handleLogin} />
              <button className="form-button" onClick={handleToggleSignupForm}>
                Зарегистрироваться
              </button>
            </div>
          )}
          {showSignupForm && (
            <div className="form-group">
              <SignupForm />
              <button className="form-button" onClick={handleToggleLoginForm}>
                Войти
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default User;
