import React, { useState } from 'react';
import CreateForm from '../components/CRUD/CreateForm';
import ReadForm from '../components/CRUD/ReadForm';
import UpdateForm from '../components/CRUD/UpdateForm';
import DeleteForm from '../components/CRUD/DeleteForm';
import '../styles.css'

const AdminForm = () => {
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [showReadForm, setShowReadForm] = useState(false);
  const [showUpdateForm, setShowUpdateForm] = useState(false);
  const [showDeleteForm, setShowDeleteForm] = useState(false);

  const handleToggleCreateForm = () => {
    setShowCreateForm(!showCreateForm);
    setShowReadForm(false);
    setShowUpdateForm(false);
    setShowDeleteForm(false);
  };

  const handleToggleReadForm = () => {
    setShowReadForm(!showReadForm);
    setShowCreateForm(false);
    setShowUpdateForm(false);
    setShowDeleteForm(false);
  };

  const handleToggleUpdateForm = () => {
    setShowUpdateForm(!showUpdateForm);
    setShowCreateForm(false);
    setShowReadForm(false);
    setShowDeleteForm(false);
  };

  const handleToggleDeleteForm = () => {
    setShowDeleteForm(!showDeleteForm);
    setShowCreateForm(false);
    setShowReadForm(false);
    setShowUpdateForm(false);
  };

  return (
    <div className='admin-title'>
      <h2>Управление турами</h2>
      <div className="button-group">
        <button onClick={handleToggleCreateForm}>Создать запись</button>
        <button onClick={handleToggleReadForm}>Просмотреть записи</button>
        <button onClick={handleToggleUpdateForm}>Обновить запись</button>
        <button onClick={handleToggleDeleteForm}>Удалить запись</button>
      </div>
      {showCreateForm && <CreateForm />}
      {showReadForm && <ReadForm />}
      {showUpdateForm && <UpdateForm />}
      {showDeleteForm && <DeleteForm />}
    </div>
  );
};

export default AdminForm;
