import React from 'react';



const ReadForm = ({ records, handleToggleUpdateForm, handleToggleDeleteForm }) => {
  return (
    <div>
      <h2>Просмотр записей</h2>
      <table>
        <thead>
          <tr>
            <th>Название</th>
            <th>Описание</th>
            <th>Действия</th>
          </tr>
        </thead>
        <tbody>
          {records.map((record) => (
            <tr key={record.id}>
              <td>{record.title}</td>
              <td>{record.description}</td>
              <td>
                <button onClick={() => handleToggleUpdateForm(record.id)}>Update</button>
                <button onClick={() => handleToggleDeleteForm(record.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ReadForm;
