import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button } from 'react-bootstrap';

const API_URL = 'http://localhost:5000';

const UpdateForm = ({ recordId, onCancel }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    duration: '',
    activity: '',
    date: [],
    price: '',
  });

  useEffect(() => {
    // Загрузка данных записи при монтировании компонента
    fetchRecordData();
  }, []);

  const fetchRecordData = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/equipment/${recordId}`);
      const recordData = response.data;
      setFormData({
        title: recordData.title,
        description: recordData.description,
        duration: recordData.duration,
        activity: recordData.activity,
        date: recordData.date,
        price: recordData.price,
      });
    } catch (error) {
      console.log('Ошибка загрузки данных записи', error);
    }
  };

  const handleInputChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      await axios.put(`${API_URL}/api/equipment/${recordId}`, formData);

      alert('Запись успешно обновлена');
      onCancel();
    } catch (error) {
      console.log('Ошибка обновления записи', error);
    }
  };

  return (
    <div className="update-form-container">
      <h2>Изменить запись</h2>
      <Form onSubmit={handleSubmit} className="update-form">
        <Form.Group>
          <Form.Label>Название:</Form.Label>
          <Form.Control type="text" name="title" value={formData.title} onChange={handleInputChange}  />
        </Form.Group>
        <Form.Group>
          <Form.Label>Описание:</Form.Label>
          <Form.Control type="text" name="description" value={formData.description} onChange={handleInputChange}  />
        </Form.Group>
       
        <Form.Group>
          <Form.Label>Дата:</Form.Label>
          <Form.Control type="text" name="date" value={formData.date} onChange={handleInputChange}  />
        </Form.Group>
        <Form.Group>
          <Form.Label>Цена:</Form.Label>
          <Form.Control type="text" name="price" value={formData.price} onChange={handleInputChange}  />
        </Form.Group>
        <Button type="submit" className="btn btn-primary">Сохранить</Button>
        <Button variant="secondary" onClick={onCancel}>Отмена</Button>
        </Form>
        </div>
        );
        };
        export default UpdateForm;
