import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:5000';

const TourDetailsPage = ({ match }) => {
  const [tour, setTour] = useState(null);

  useEffect(() => {
    const fetchTourDetails = async () => {
      try {
        const response = await axios.get(`${API_URL}/api/my_tour/${match.params.tourId}`);
        setTour(response.data);
      } catch (error) {
        console.log('Ошибка получения данных тура', error);
      }
    };

    fetchTourDetails();
  }, [match.params.tourId]);

  return (
    <div className="tour-details-page">
      {tour ? (
        <div className="tour-details">
          <h1>{tour.title}</h1>
          <p>{tour.description}</p>
          <a href={`${API_URL}/uploads/${tour.document}`} target="_blank" rel="noopener noreferrer">
            Ссылка на документ
          </a>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default TourDetailsPage;
