import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const API_URL = 'http://localhost:5000';

const TourDetailsPage = () => {
  const { tourId } = useParams();
  const [tour, setTour] = useState(null);

  useEffect(() => {
    const fetchTourDetails = async () => {
      try {
        const response = await axios.get(`${API_URL}/api/tours/${tourId}`);
        const tourData = response.data;
        setTour(tourData);
      } catch (error) {
        console.log('Ошибка получения информации о туре', error);
      }
    };

    fetchTourDetails();
  }, [tourId]);

  const handleDownload = () => {
    if (tour && tour.filePath) {
      window.open(`${API_URL}/${tour.filePath}`, '_blank');
    }
  };

  if (!tour) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>{tour.title}</h2>
      <p>{tour.description}</p>
      <p>Длительность: {tour.duration}</p>
      <p>Вид активности: {tour.activity}</p>
      <p>Даты: {tour.date.join(', ')}</p>
      <p>Цена: {tour.price}</p>
      {tour.filePath && (
        <div>
          <h4>Документ</h4>
          <button onClick={handleDownload}>Скачать</button>
        </div>
      )}
    </div>
  );
};

export default TourDetailsPage;
