import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './UpdateForm.css';

const API_URL = 'http://localhost:5000';

const UpdateForm = () => {
  const [records, setRecords] = useState([]);
  const [selectedRecordId, setSelectedRecordId] = useState('');
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    image: null,
    duration: '',
    activity: '',
    date: '',
    price: ''
  });

  useEffect(() => {
    const fetchRecords = async () => {
      try {
        const response = await axios.get(`${API_URL}/api/my_tour`);
        setRecords(response.data);
      } catch (error) {
        console.log('Ошибка получения записей', error);
      }
    };

    fetchRecords();
  }, []);

  const handleRecordChange = (event) => {
    const selectedId = event.target.value;
    setSelectedRecordId(selectedId);

    const selectedRecord = records.find((record) => record.id === selectedId);
    if (selectedRecord) {
      setFormData({
        title: selectedRecord.title,
        description: selectedRecord.description,
        image: null,
        duration: selectedRecord.duration,
        activity: selectedRecord.activity,
        date: selectedRecord.date,
        price: selectedRecord.price
      });
    } else {
      setFormData({
        title: '',
        description: '',
        image: null,
        duration: '',
        activity: '',
        date: '',
        price: ''
      });
    }
  };

  const handleInputChange = (event) => {
    if (event.target.name === 'image') {
      setFormData({
        ...formData,
        [event.target.name]: event.target.files[0]
      });
    } else {
      setFormData({
        ...formData,
        [event.target.name]: event.target.value
      });
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const token = localStorage.getItem('token');
    const config = {
      headers: {
        'Content-Type': 'multipart/form-data',
        Authorization: `Bearer ${token}`
      }
    };

    const formDataToSend = new FormData();
    formDataToSend.append('title', formData.title);
    formDataToSend.append('description', formData.description);
    formDataToSend.append('image', formData.image);
    formDataToSend.append('duration', formData.duration);
    formDataToSend.append('activity', formData.activity);
    formDataToSend.append('date', formData.date);
    formDataToSend.append('price', formData.price);

    try {
      await axios.put(`${API_URL}/api/my_tour/${selectedRecordId}`, formDataToSend, config);
      // Здесь можно использовать компонент или состояние для отображения сообщения об успешном обновлении записи
      alert('Запись успешно обновлена');
      setSelectedRecordId('');
      setFormData({
        title: '',
        description: '',
        image: null,
        duration: '',
        activity: '',
        date: '',
        price: ''
      });
    } catch (error) {
      console.log('Ошибка обновления записи', error);
    }
  };

  return (
    <div className="update-form-container">
      <h2>Изменить запись</h2>
      <div>
        <select value={selectedRecordId} onChange={handleRecordChange}>
          <option value="">Выберите запись</option>
          {records.map((record) => (
            <option key={record.id} value={record.id}>
              {record.name}
            </option>
          ))}
        </select>
      </div>
      {selectedRecordId && (
        <form onSubmit={handleSubmit} className="update-form">
          <div className="form-group">
            <label>Название:</label>
            <input type="text" name="title" value={formData.title} onChange={handleInputChange} required />
          </div>
          <div className="form-group">
            <label>Описание:</label>
            <textarea name="description" value={formData.description} onChange={handleInputChange} required />
          </div>
          <div className="form-group">
            <label>Изображение:</label>
            <input type="file" name="image" onChange={handleInputChange} required />
          </div>
          <div className="form-group">
            <label>Длительность:</label>
            <input type="text" name="duration" value={formData.duration} onChange={handleInputChange} required />
          </div>
          <div className="form-group">
            <label>Вид активности:</label>
            <input type="text" name="activity" value={formData.activity} onChange={handleInputChange} required />
          </div>
          <div className="form-group">
            <label>Дата:</label>
            <input type="date" name="date" value={formData.date} onChange={handleInputChange} required />
          </div>
          <div className="form-group">
            <label>Цена:</label>
            <input type="text" name="price" value={formData.price} onChange={handleInputChange} required />
          </div>
          <button type="submit" className="btn-update">
            Сохранить
          </button>
        </form>
      )}
    </div>
  );
};

export default UpdateForm;
