import React, { useState } from 'react';
import CreateForm from '../components/CRUD/CreateForm';
import ReadForm from '../components/CRUD/ReadForm';
import UpdateForm from '../components/CRUD/UpdateForm';
import DeleteForm from '../components/CRUD/DeleteForm';
import '../styles.css';

const AdminForm = () => {
  const [formState, setFormState] = useState({
    create: false,
    read: false,
    update: false,
    delete: false,
  });
  const [selectedRecordId, setSelectedRecordId] = useState('');

  const handleToggleForm = (formType, recordId = '') => {
    setSelectedRecordId(recordId);
    setFormState({
      create: false,
      read: false,
      update: false,
      delete: false,
      [formType]: true,
    });
  };

  return (
    <div className="admin-title">
      <h2>Управление турами</h2>
      <div className="button-group">
        <button onClick={() => handleToggleForm('create')}>Создать запись</button>
        <button onClick={() => handleToggleForm('read')}>Просмотреть записи</button>
        {formState.read && selectedRecordId && (
          <>
            <button onClick={() => handleToggleForm('update', selectedRecordId)}>Изменить запись</button>
            <button onClick={() => handleToggleForm('delete', selectedRecordId)}>Удалить запись</button>
          </>
        )}
      </div>

      {formState.create && <CreateForm />}
      {formState.read && (
        <ReadForm
          handleToggleUpdateForm={handleToggleForm}
          handleToggleDeleteForm={handleToggleForm}
        />
      )}
      {formState.update && (
        <UpdateForm
          recordId={selectedRecordId}
          onCancel={() => handleToggleForm('read')}
        />
      )}
      {formState.delete && (
        <DeleteForm
          selectedRecordId={selectedRecordId}
          onCancel={() => handleToggleForm('read')}
        />
      )}
    </div>
  );
};

export default AdminForm;
