import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:5000';

const ReadForm = ({ handleToggleUpdateForm, handleToggleDeleteForm }) => {
  const [records, setRecords] = useState([]);

  useEffect(() => {
    fetchRecords();
  }, []);

  const fetchRecords = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/my_tour`);
      setRecords(response.data);
    } catch (error) {
      console.log('Ошибка получения записей', error);
    }
  };

  const handleDeleteRecord = async (id) => {
    try {
      await axios.delete(`${API_URL}/api/my_tour/${id}`);
      // Обновление списка записей после удаления
      fetchRecords();
    } catch (error) {
      console.log('Ошибка при удалении записи', error);
    }
  };

  const handleUpdateRecord = async (id) => {
    try {
      // Здесь вам нужно получить данные для обновления из формы или других источников
      const data = {
        // Передайте необходимые поля для обновления
      };

      await axios.put(`${API_URL}/api/my_tour/${id}`, data);
      // Обновление списка записей после обновления
      fetchRecords();
    } catch (error) {
      console.log('Ошибка при обновлении записи', error);
    }
  };

  return (
    <div>
      <h2>Просмотр записей</h2>
      <table>
        <thead>
          <tr>
            <th>Название</th>
            <th>Описание</th>
            <th>Действия</th>
          </tr>
        </thead>
        <tbody>
          {records.map((record) => (
            <tr key={record.id}>
              <td>{record.title}</td>
              <td>{record.description}</td>
              <td>
                <button onClick={() => handleToggleUpdateForm(record.id)}>Изменить</button>
              </td>
              <td>
                <button onClick={() => handleDeleteRecord(record.id)}>Удалить</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ReadForm;
