import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TourRegistrationPage.css';

const API_URL = 'http://localhost:5000';

const validationRules = {
  date: {
    required: true,
    message: 'Введите дату тура',
  },
  name: {
    required: true,
    message: 'Введите имя',
  },
  lastname: {
    required: true,
    message: 'Введите фамилию',
  },
  number: {
    required: true,
    message: 'Введите номер телефона',
  },
  paymentMethod: {
    required: true,
    message: 'Выберите способ оплаты',
  },
};

const fieldNames = {
  date: 'Дата тура',
  name: 'Имя',
  lastname: 'Фамилия',
  number: 'Номер телефона',
  paymentMethod: 'Способ оплаты',
};

const TourRegistrationPage = ({ match }) => {
  const tourId = match.params.tourId;
  const [userId, setUserId] = useState(null);
  const [tourData, setTourData] = useState(null);
  const [participantData, setParticipantData] = useState({
    date: '',
    name: '',
    lastname: '',
    number: '',
    paymentMethod: '',
  });
  const [errors, setErrors] = useState({});

  useEffect(() => {
    getTourInfoFromDatabase();
  }, []);

  const getTourInfoFromDatabase = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/my_tour/${tourId}`);
      const responseUser = await axios.get(`${API_URL}/api/user`);
      setTourData(response.data);
      setUserId(responseUser.data.id);
    } catch (error) {
      console.error('Ошибка получения данных тура', error);
    }
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setParticipantData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const validationErrors = validateForm();
    if (Object.keys(validationErrors).length === 0) {
      try {
        const data = { ...participantData, user_id: userId };
        await axios.post(`${API_URL}/api/participants`, data);
        // Дополнительные действия после успешной отправки формы
      } catch (error) {
        console.error('Ошибка отправки формы', error);
      }
    } else {
      setErrors(validationErrors);
    }
  };
  

  const validateForm = () => {
    const errors = {};

    Object.entries(validationRules).forEach(([fieldName, rules]) => {
      if (rules.required && !participantData[fieldName]) {
        errors[fieldName] = rules.message;
      }
    });

    return errors;
  };

  return (
    <div className="tour-registration-page">
      {tourData && <h2>Форма записи на тур: {tourData.title}</h2>}
      <form onSubmit={handleSubmit} className="tour-registration-form">
        {Object.entries(validationRules).map(([fieldName, rules]) => (
          <div className="form-group" key={fieldName}>
            <label>{fieldNames[fieldName]}:</label>
            {fieldName === 'paymentMethod' ? (
              <select
                name={fieldName}
                value={participantData[fieldName]}
                onChange={handleInputChange}
              >
                <option value="">Выберите способ оплаты</option>
                <option value="карта">Оплата по картам</option>
                <option value="телефон">Оплата на баланс телефона</option>
              </select>
            ) : (
              <input
                type={fieldName === 'date' ? 'date' : 'text'}
                name={fieldName}
                value={participantData[fieldName]}
                onChange={handleInputChange}
              />
            )}
            {errors[fieldName] && <div className="error-message">{errors[fieldName]}</div>}
          </div>
        ))}
        <button type="submit" className="button-anim">
          Записаться
        </button>
      </form>
    </div>
  );
};

export default TourRegistrationPage;
