import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Tour.css';
import TourRegistrationForm from './components/TourRegistrationForm';
import ReCAPTCHA from 'react-google-recaptcha';

const RECAPTCHA_KEY = '6LdyP68mAAAAAL1uzPKNA0CLU55_c2OXKsQMIyjp';

const API_URL = 'http://localhost:5000';

const Tours = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    title: '',
    description: '',
    image: '',
    duration: '',
    activity: '',
    date: [],
    price: ''
  });
  const [dateFilter, setDateFilter] = useState('');
  const [toursData, setToursData] = useState([]);
  const [selectedTour, setSelectedTour] = useState(null);
  const [captchaVisible, setCaptchaVisible] = useState(false);
  const [captchaPassed, setCaptchaPassed] = useState(false);

  const downloadFileWithCaptcha = async (fileUrl) => {
    try {
      await axios.post(`${API_URL}/api/captcha`, { response: captchaPassed });
      window.location.href = fileUrl;
    } catch (error) {
      console.error('Ошибка проверки капчи', error);
    }
  };

  useEffect(() => {
    const fetchToursData = async () => {
      try {
        const response = await axios.get(`${API_URL}/api/my_tour`);
        const formattedToursData = response.data.map((tour) => ({
          ...tour,
          date: tour.date.map((date) => new Date(date).toISOString().split('T')[0])
        }));
        setToursData(formattedToursData);
      } catch (error) {
        console.log('Ошибка получения данных туров', error);
      }
    };

    fetchToursData();
  }, []);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleFilterChange = (event) => {
    const { name, value } = event.target;
    if (name === 'date') {
      const formattedDate = new Date().toISOString().split('T')[0];
      setDateFilter(value !== '' ? [value] : []);
    } else {
      setFilters((prevFilters) => ({ ...prevFilters, [name]: value }));
    }
  };

  const filterTours = (tours) => {
    return tours.filter((tour) => {
      const { duration, activity, price } = filters;
      const searchTermMatch =
        tour.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        tour.description.toLowerCase().includes(searchTerm.toLowerCase());

      return (
        (duration === '' || tour.duration.toLowerCase().includes(duration.toLowerCase())) &&
        (activity === '' || tour.activity.toLowerCase().includes(activity.toLowerCase())) &&
        (dateFilter.length === 0 || tour.date.some((date) => date === dateFilter[0])) &&
        (price === '' || tour.price === price) &&
        searchTermMatch
      );
    });
  };

  const filteredTours = filterTours(toursData);

  const handleCaptchaChange = (value) => {
    setCaptchaPassed(true);
  };

  const handleCaptchaClick = async () => {
    if (captchaPassed && selectedTour) {
      try {
        const fileUrl = `${API_URL}/uploads/${selectedTour.file}`;
        await axios.post(`${API_URL}/api/captcha`, { response: captchaPassed });
        downloadFileWithCaptcha(fileUrl);
      } catch (error) {
        console.error('Ошибка проверки капчи', error);
      }
    } else {
      setCaptchaVisible(true);
    }
  };

  return (
    <div className="tours-page">
      <div className="tours-title">
        <h1>Туры</h1>
      </div>
      <div className="search-filter">
        <input
          type="text"
          placeholder="Поиск..."
          value={searchTerm}
          onChange={handleSearch}
          className="search-input"
        />
        <div className="filter-group">
          <label>Длительность:</label>
          <select
            name="duration"
            value={filters.duration}
            onChange={handleFilterChange}
            className="filter-select"
          >
            <option value="">Все длительности</option>
            <option value="3 дня">3 дня</option>
            <option value="5 дней">5 дней</option>
            <option value="7 дней">7 дней</option>
          </select>
        </div>
        <div className="filter-group">
          <label>Активность:</label>
          <select
            name="activity"
            value={filters.activity}
            onChange={handleFilterChange}
            className="filter-select"
          >
            <option value="">Все активности</option>
            <option value="пеший">Пеший</option>
            <option value="велосипедный">Велосипедный</option>
            <option value="верховой">Верховой</option>
          </select>
        </div>
        <div className="filter-group">
          <label>Дата:</label>
          <input
            type="date"
            name="date"
            value={filters.date.length > 0 ? filters.date[0] : ''}
            onChange={handleFilterChange}
            className="filter-input"
          />
        </div>
        <div className="filter-group">
          <label>Цена:</label>
          <input
            type="text"
            placeholder="Цена"
            name="price"
            value={filters.price}
            onChange={handleFilterChange}
            className="filter-input"
          />
        </div>
      </div>
      <div className="tour-container">
        {filteredTours.map((tour) => (
          <div className="tour-card" key={tour.tour_id}>
            <div className="tour-image-container">
              <img
                src={`${API_URL}/uploads/${tour.image}`}
                alt={tour.title}
                className="tour-image"
              />
              <div className="tour-dates">
                {tour.date.map((date) => (
                  <span key={date} className="tour-date">
                    {date}
                  </span>
                ))}
              </div>
            </div>
            <span className="tour-price">{tour.price}</span>
            <div className="tour-details">
              <h3>{tour.title}</h3>
              <p>{tour.description}</p>
              <div className="buttons-container">
              {!captchaPassed && (
                    <>
                  <button onClick={() => handleRegistrationClick(tour)} className="button-anim">
                    Записаться на тур
                  </button>
                      <button onClick={() => setCaptchaVisible(true)} className="button-anim">
                      Получить информацию
                      </button>
                      {captchaVisible && (
                        <ReCAPTCHA
                          sitekey={'6LdyP68mAAAAALgzaCgACM-PO-uDRMmKdiwpBvWF'}
                          onChange={handleCaptchaChange}
                        />
                      )}
                    </>
                  )}

                  {captchaPassed && (
                    <button onClick={() => downloadFileWithCaptcha(`${API_URL}/uploads/${tour.file}`)} className="button-anim">
                      Скачать файл
                    </button>
                  )}

              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Tours;
