import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Equipment.css';
import ReCAPTCHA from 'react-google-recaptcha';

const RECAPTCHA_KEY = '6LdyP68mAAAAAL1uzPKNA0CLU55_c2OXKsQMIyjp';
const API_URL = 'http://localhost:5000';

const Equipment = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    title: '',
    description: '',
    image: '',
    category: '',
    price: ''
  });
  const [equipmentData, setEquipmentData] = useState([]);
  const [captchaVisible, setCaptchaVisible] = useState(false);
  const [captchaPassed, setCaptchaPassed] = useState(false);

  useEffect(() => {
    const fetchEquipmentData = async () => {
      try {
        const response = await axios.get(`${API_URL}/api/equipment`);
        setEquipmentData(response.data);
      } catch (error) {
        console.log('Ошибка получения данных оборудования', error);
      }
    };

    fetchEquipmentData();
  }, []);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleFilterChange = (event) => {
    const { name, value } = event.target;
    setFilters((prevFilters) => ({ ...prevFilters, [name]: value }));
  };

  const filterEquipment = (equipment) => {
    return equipment.filter((item) => {
      const { category, price } = filters;
      const searchTermMatch =
        item.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        item.description.toLowerCase().includes(searchTerm.toLowerCase());

      return (
        (category === '' || item.category.toLowerCase().includes(category.toLowerCase())) &&
        (price === '' || item.price === price) &&
        searchTermMatch
      );
    });
  };

  const filteredEquipment = filterEquipment(equipmentData);

  const handleCaptchaChange = (value) => {
    setCaptchaPassed(true);
  };

  return (
    <div className="equipment-page">
      <div className="equipment-title">
        <h1>Туры</h1>
      </div>
      <div className="search-filter">
        <input
          type="text"
          placeholder="Поиск по названию..."
          value={searchTerm}
          onChange={handleSearch}
          className="search-input"
        />
        <div className="filter-group">
          <label>Название:</label>
          <input
            type="text"
            placeholder="Название снарежения"
            name="title"
            value={filters.title}
            onChange={handleFilterChange}
            className="filter-input"
          />
        </div>
        <div className="filter-group">
          <label>Описание:</label>
          <input
            type="text"
            placeholder="Описание"
            name="description"
            value={filters.description}
            onChange={handleFilterChange}
            className="filter-input"
          />
        </div>
      </div>
      <div className="equipment-container">
        {filteredEquipment.map((item) => (
          <div className="equipment-card" key={item.equipment_id}>
            <div className="equipment-image-container">
              <img
                src={`${API_URL}/uploads/${item.image}`}
                alt={item.title}
                className="equipment-image"
              />
            </div>
            <span className="equipment-price">{item.price}</span>
            <div className="equipment-details">
              <h3>{item.title}</h3>
              <p>{item.description}</p>
              <button onClick={() => setCaptchaVisible(true)} className="button-anim">
                Узнать подробнее
              </button>
              {captchaVisible && (
                <ReCAPTCHA
                  sitekey={RECAPTCHA_KEY}
                  onChange={handleCaptchaChange}
                />
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Equipment;
