import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';

const TourDetailsPage = () => {
  const { tourId } = useParams();

  useEffect(() => {
    // Здесь вы можете добавить код для загрузки данных о туре, если это необходимо
    // Например, использовать fetch или другой метод получения данных с сервера
    // И обновить состояние компонента, чтобы отобразить данные тура, если требуется
  }, [tourId]);

  const handleDownload = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/my_tour`);
      const blob = await response.blob();

      // Создаем ссылку на файл и автоматически кликаем по ней для скачивания файла
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = 'filename.extension'; // Замените на имя файла и расширение, которые вы хотите использовать
      link.click();

      // Освобождаем ресурсы URL объекта
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Произошла ошибка при скачивании файла:', error);
    }
  };

  return (
    <div>
      <button onClick={handleDownload}>Скачать файл</button>
    </div>
  );
};

export default TourDetailsPage;
