import React, { useState } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:5000';

const DeleteForm = ({ recordId, handleDeleteRecord }) => {
  const [isLoading, setIsLoading] = useState(false);
  const [isDeleted, setIsDeleted] = useState(false);


  const handleDelete = async () => {
    setIsLoading(true);

    try {
      await handleDeleteRecord(recordId);
      setIsDeleted(true);
      setTimeout(() => setIsDeleted(false), 3000); // Reset the deletion status after 3 seconds
    } catch (error) {
      console.log('Ошибка удаления записи', error);
    }

    setIsLoading(false);
  };
    
    return (
      <div>
        <h2>Удалить запись</h2>
        {isDeleted ? (
          <p>Запись успешно удалена</p>
        ) : (
          <>
            <p>Вы действительно хотите удалить эту запись?</p>
            <button onClick={handleDelete} disabled={isLoading}>
              {isLoading ? 'Удаление...' : 'Удалить'}
            </button>
          </>
        )}
      </div>
    );
  };

export default DeleteForm;
